mapper.select:
select

from APABI_AUTH_BOOKLIST pb
WHERE 1=1

  AND pb.COPYRIGHT_OWNER_ID = #{copyrightOwnerId}


  AND pb.BOOK_LIST_NUM = #{bookListNum}


  AND pb.AUTHORIZE_NUM = #{authorizeNum}


  AND pb.COOPERTOR LIKE CONCAT(CONCAT('%',#{coopertor,jdbcType=VARCHAR}),'%')


  AND pb.AUTHEND_DATE >= #{authEndDate}


  AND pb.AUTHEND_DATE < #{authEndDate1}


  AND pb.SUBMIT_DATE >= #{submitDate}


  AND pb.SUBMIT_DATE < #{submitDate1}
