mapper.select:
select

from APABI_COPYRIGHT_AGREEMENT pb
WHERE 1=1

  AND pb.COPYRIGHT_OWNER_ID = #{copyrightOwnerId}


  AND pb.AUTH_TYPE = #{authType}


  AND pb.CONTENT_MANAGER_NAME LIKE CONCAT(CONCAT('%',#{contentManagerName,jdbcType=VARCHAR}),'%')


  AND pb.START_DATE >= #{startDate}


  AND pb.START_DATE < #{startDate1}


  AND pb.END_DATE >= #{endDate}


  AND pb.END_DATE < #{endDate1}
