mapper.select:
select
,
from APABI_BOOK_PAGE where META_ID = #{metaId,jdbcType=VARCHAR} AND PAGE_ID = #{pageId,jdbcType=DECIMAL}

mapper.insert:
begin
insert into APABI_BOOK_PAGE (ID, CREATE_TIME, META_ID, PAGE_ID, UPDATE_TIME, WORD_SUM, CONTENT) values (#{item.id,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.metaId,jdbcType=VARCHAR}, #{item.pageId,jdbcType=DECIMAL}, #{item.updateTime,jdbcType=TIMESTAMP}, #{item.wordSum,jdbcType=DECIMAL}, #{item.content,jdbcType=CLOB})
;end;
